/**
 * Index
 * @summary The home page of our website.
 */

import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import Button from 'react-bootstrap/Button';

const Home = props => {
    return (
        <Layout>
            {/* <h1>Media Tracker</h1>
            <p>Home page</p> */}
            <p>{`Res: ${props.result}`}</p>
            <br />
            <LoadingButton>Check in</LoadingButton>
        </Layout>
    );
};

const displayNames = props => {
    return (
        <p>{props.names}</p>
    );
}

const LoadingButton = () => {
    const [isLoading, setLoading] = useState(false);
    const [isClicked, setClicked] = useState(false);
    const [names, setNames] = useState("");

    useEffect(() => {
        if (isLoading) {
            checkIn().then(props => {
                setNames(names + props.result);
                setClicked(true);
                setLoading(false);
            });
        }
    }, [isLoading]);

    const handleClick = () => setLoading(true);

    return (
        <div>
            <Button
                variant="primary"
                disabled={isLoading}
                onClick={!isLoading ? handleClick : null}
            >
                {isLoading ? 'Loading…' : 'Click to load'}
            </Button>
            {
                isClicked ?
                    <p><br />{names}</p>
                    :
                    null
            }
        </div>
    );
}

const checkIn = async () => {
    var checkedIn = "";

    try {
        const res = axios.all([
            axios.get('http://localhost:5000/check-fabian'),
            axios.get('http://localhost:5000/check-miguel')
        ]).then(axios.spread(function (fabian, miguel) {
            checkedIn = fabian.data.Success + miguel.data.Success;
        }));
        return { result: res.data }
    } catch (error) {
        console.error(error);
        return { result: 'Failed to check in members' }
    }
}

Home.getInitialProps = async () => {
    try {
        // call server at endpoint /search-title
        const res = await axios.get('http://localhost:5000/ping');
        // res.data is the JSON object returned which is { Success: ***data*** }
        return { result: res.data.Success }
    } catch (error) {
        console.error(error);
        return { result: 'Failed to fetch data from server' }
    }
};

export default Home;
